mapper.select:
select

from tb_driver_sfz t


		and t.id = #{id}			


		and t.driver_id = #{driverId}			


		and t.atta_name = #{attaName}			


		and t.atta_path = #{attaPath}			


		and t.ul_user_name = #{ulUserName}			


		and t.ul_user_id = #{ulUserId}			


		and t.ul_time = #{ulTime}

select

from tb_driver_sfz t
where t.id = #{value}

mapper.update:
update tb_driver_sfz
driver_id = #{driverId}, atta_name = #{attaName}, atta_path = #{attaPath}, ul_user_name = #{ulUserName}, ul_user_id = #{ulUserId}, ul_time = #{ulTime},
where id = #{id}
